import './Calendar.css'

import {useState} from 'react';
import ButtonCalendar from './ButtonCalendar';

const Calendar = () => {


    const [monthStep,setMonthStep]=useState(0)


    let state = {
        activeDate: new Date()
    }


    let daysName=['ПН','ВТ','СР','ЧТ','ПТ','СБ','ВС']
    let nDays = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];
    var year = state.activeDate.getFullYear();
    var month =state.activeDate.getMonth();
    var firstDay = new Date(year, month, 1).getDay();
    let dayInMonth=nDays[month]

    const [y,setY]=useState(year)
    const [m,setM]=useState(month)
    
    function calculateCalendar(step=0){
       
        month=month+step


  
        if(month == 12){
            month=0
            year+=1

        
        }
       if (month  == -1 ){
            year-=1
            month=11
 
        }

        firstDay = new Date(year, month, 1).getDay();

        let matrix=[]
        
        let label=firstDay-1
        
        let num=1;
        matrix[0]=daysName
        
        for(let i=1;i<7;i++ ){
            matrix[i]=[];
            if(i !=1) label=0
            for(let j=0;j<7;j++ ){
                
                if(label !=0 && label>j){
                    matrix[i][j]="#";
                }
                else if(num<=dayInMonth){
                    matrix[i][j]=num;
                    num++
                }
                else{
                   
                    matrix[i][j]="#"
                }
        
            }

        }
        
        const array=[]
        matrix.forEach((i)=>{
            i.map((e)=>{
              array.push(<ButtonCalendar val={e}/>)
               
           })
          })



        setArr(array)
        return array
    }
   


    const [arr,setArr]=useState( calculateCalendar(monthStep))

    return ( 
    
        <div className="Calendar">
      
        <p className="data">{y} {m }</p>

        <div className="array">
        {arr.map((e)=>{
        return <ButtonCalendar num={e}/>
    })
        }
        </div>

        <button onClick={()=>setMonthStep(monthStep-1)} id="btnPrev" type="button">Предыдущий</button>
            <button onClick={()=>setMonthStep(monthStep+1)} id="btnNext" type="button">Следующий</button>
        <div id="divCal"></div>


        </div>
        
        
        
    );
}
 
export default Calendar;
