import { tab } from '@testing-library/user-event/dist/tab';
import './Calendar.css'

import * as React from 'react';
import ButtonCalendar from './ButtonCalendar';

const Calendar = () => {

    
let state = {
    activeDate: new Date()
}

let daysName=['ПН','ВТ','СР','ЧТ','ПТ','СБ','ВС']
let nDays = [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];

        
        
var year = state.activeDate.getFullYear();
var month =state.activeDate.getMonth();
var firstDay = new Date(year, month, 1).getDay();
let dayInMonth=nDays[month]

let matrix=[]

let label=firstDay-1

let num=1;

for(let i=0;i<6;i++ ){
    matrix[i]=[];
    if(i !=0) label=0
    for(let j=label;j<7;j++ ){
        
        if(num<=dayInMonth){
            matrix[i][j]=num;
            num++
        }
        else{
           
            matrix[i][j]=<ButtonCalendar/>
        }

    }
}

for(let i=0;i<6;i++ ){

    console.log(matrix[i])
}




    return ( 
    
        <div className="Calendar">

        {matrix.forEach((e)=>{
        <ButtonCalendar num={e}/>
})}
        <button id="btnPrev" type="button">Предыдущий</button>
            <button id="btnNext" type="button">Следующий</button>
        <div id="divCal"></div>


        </div>
        
        
        
    );
}
 
export default Calendar;
