import './Calendar.css'

import { useState } from 'react';
import ButtonCalendar from './ButtonCalendar';

const Calendar = () => {
    const [monthStep, setMonthStep] = useState(0)

    const [nowData,setNowData]=useState( {activeDate: new Date()})
    const [daysName,setDaysName]=useState(['ПН', 'ВТ', 'СР', 'ЧТ', 'ПТ', 'СБ', 'ВС'])
    const [nDays,setNdDys]=useState( [31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31])
    const [year,setYear]=useState(nowData.activeDate.getFullYear())
    const [month,setMonth]=useState(nowData.activeDate.getMonth())


    var firstDay = new Date(year, month, 1).getDay();
    let dayInMonth = nDays[month]

    const [y, setY] = useState(year)
    const [m, setM] = useState(month)


    function calculateCalendar(step = 0) {

        month = month + step

        if (month == 12) {
            month = 0
            year += 1

        }
        if (month == -1) {
            year -= 1
            month = 11
            

        }
        
        firstDay = new Date(year, month, 1).getDay();


        let matrix = []

        let label = firstDay - 1

        let num = 1;
        matrix[0] = daysName

        for (let i = 1; i < 7; i++) {
            matrix[i] = [];
            if (i != 1) label = 0
            for (let j = 0; j < 7; j++) {

                if (label != 0 && label > j) {
                    matrix[i][j] = "#";
                }
                else if (num <= dayInMonth) {
                    matrix[i][j] = num;
                    num++
                }
                else {

                    matrix[i][j] = "#"
                }

            }

        }

        const array = []
        matrix.forEach((i) => {
            i.map((e) => {
                array.push(<ButtonCalendar val={e} />)

            })
        })
        return array
    }

    function changeCalendar(step) {
        setMonthStep(monthStep + step)
        setM(month)
        setY(year)
        setAr(calculateCalendar(monthStep))
        
    }
    const [ar, setAr] = useState(calculateCalendar(monthStep))


    return (

        <div className="Calendar">

            <p className="data">{y} {m}</p>

            <div className="array">
                {ar.map((e) => {
                    return <ButtonCalendar num={e} />
                })
                }
            </div>

            <button onClick={() => changeCalendar(-1)} id="btnPrev" type="button">Предыдущий</button>
            <button onClick={() => changeCalendar(1)} id="btnNext" type="button">Следующий</button>
            <div id="divCal"></div>


        </div>



    );
}

export default Calendar;
