import { useState } from 'react';
import './styles/App.css';
import Modal from './components/Modal';

function App() {

  const [date,setDate]=useState('Сегодня')

  const [modal,setModal]=useState(false)


  return (
    <div className="App">
        <Modal visible={modal}  setVisible={setModal}>
          <input/>
        </Modal>


      <header className='header'><div className='header__hamb'><button className='header__btn'></button></div><h1 className='header__title'>Моя тудушечка</h1></header>
        <div className='main'>
        <div className='tasks_wrapper'>
          <h1 className='main__title'>{date}</h1> <button  onClick={()=>setModal(true)} className='main__btn'>+ Добавить</button></div>

        <div className='tasks_container'>
            <h4 className='tasks__title'>Личные</h4>
           <ul className='tasks__list'>

            <ol className='tasks__item'><input className='tasks__input' type='radio'/> 
            <div className='tasks__content'>

            <h5 className='tasks__item-title'>Поспать</h5>
            <p className='tasks__item-time'>5:30</p>
              </div>
              </ol>

           </ul>

        </div>

        </div>
    </div>
  );
}

export default App;
