import cl from './Modal.module.css'
import React from 'react';

function Modal ({children,visible,setVisible}) {

    const rootClasses=[cl.myModal]
        if(visible){
        rootClasses.push(cl.active)
    }
    return (  
        <>
          {children}
          </>
        
        );
}
 
export default Modal;
