import React from 'react';

const Sidebar = ({setNow,setFastAdd,setVisible}) => {
      
  let state = {
    activeDate: new Date()
}
    var year = state.activeDate.getFullYear();
    var month = state.activeDate.getMonth();
    var firstDay = new Date(year, month, 1).getDay();

  
    return ( 
        <div  className='Sidebar'>
        <div className='Sidebar__btns'>
        <button onClick={()=>setNow((String(firstDay)+'.'+String(month+1)+'.'+String(year)))} className='Sidebar__btn Sidebar__btn-active'>Сегодня</button>
         <button onClick={()=>setVisible(true)}  className='Sidebar__btn'>Список дел по датам</button>
         <button onClick={()=>setFastAdd(true)}  className='Sidebar__btn'>Быстрое добавление</button>
        </div>
     </div>
      );
}
 
export default Sidebar;
