import React from 'react';

const TaskItem = ({task,setPop,remove}) => {
    return ( 
        <ol onClick={()=>setPop(false)} className='tasks__item'>
          <button onClick={()=>remove(task)} className="tasks__close">X</button>
        <input  className='tasks__input' type='checkbox'/> 
      <div className='tasks__content'>
        
      <div className="task__item-top">
      <h5 className='tasks__item-title'>{task.title}</h5>
      <p className='tasks__item-time'>{task.time}</p>
      </div>
      <div className="task__item-bot">
        {task.body}
      </div>
        </div>
        </ol>
         );
}
 
export default TaskItem;
